mapper.select:
	select rn, b_num, m_name, w_num, b_title, b_regdate, b_readcnt, b_type, r_cnt
	from (select rn, b_num, m_name, w_num, b_title, b_regdate, b_readcnt, b_type, r_cnt
	        from (select rownum as rn, b_num, m_name, w_num, b_title, b_regdate, b_readCnt, b_type, r_cnt
	                from (select b_num, m_name, w_num, b_title, b_regdate, b_readCnt, b_type
	                         ,(select count(*) from reply r where b.b_num=r.b_num) as r_cnt
	                           from board b, member m
	                           where b.w_num = #{wNum} and m.m_num=b.m_num 
	                           group by b_num, m_name, w_num, b_title, b_regdate, b_readCnt, b_type
	                           order by b_num desc)
	              )
	        where rn <=#{lastItem}
	)where rn >=#{firstItem}

mapper.update:
update board set
	b_title = #{bTitle},
	b_content = #{bContent},
	b_type = #{bType}
where b_num = #{bNum}

update board set
	b_readcnt = b_readcnt+1
where b_num = #{bNum}
