mapper.update:
update calendar
set m_num=#{mNum}, w_num=#{wNum}, cal_type=#{type}, cal_start_date=#{startDate}, cal_end_date=#{endDate}, cal_title=#{title}, cal_content=#{content}, cal_annually=#{annually}, cal_monthly=#{monthly}, cal_yearCalendar=#{yearCalendar}, cal_color=#{color} 
where cal_num=#{cNum}

mapper.select:
select * from calendar where w_num=#{wNum}
and cal_start_date <= #{startDate}
and cal_end_date >= #{endDate}
